let buttonPicture=document.querySelector(".button-picture"),boxRotate=document.querySelector(".y-axis"),cvs=document.querySelector(".cvs"),buttonPrint=document.querySelector(".button-picture1"),sheetPicture=document.querySelector(".sheet-picture"),linkDownload=document.querySelector(".download");function init(){boxRotate.style.animationName="none",linkDownload.style.animationName="none",sheetPicture.style.display="none",linkDownload.style.display="none"}function webcam(){navigator.mediaDevices.getUserMedia({audio:!1,video:{width:800,height:800}}).then((function(mediaStream){let video=document.querySelector(".sourcevid");video.srcObject=mediaStream,video.onloadedmetadata=function(e){video.play()}})).catch((function(err){console.log(err.name+": "+err.message)}))}function clone(){let vivi=document.querySelector(".sourcevid"),cvs;document.querySelector(".cvs").getContext("2d").drawImage(vivi,0,0,800,800,0,0,300,170)}buttonPicture.addEventListener("click",()=>{(sheetPicture.style.display="none")?(boxRotate.style.animationName="rotate-camera-scale",webcam()):init()}),buttonPrint.addEventListener("click",()=>{clone(),boxRotate.style.animationName="rotate-camera-scale1",linkDownload.style.animationName="download-appear",cvs.style.animationName="appears-cvs",sheetPicture.style.display="block",linkDownload.style.display="block"}),linkDownload.addEventListener("click",()=>{linkDownload.href=cvs.toDataURL(),linkDownload.download="screen.png"});